import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';

const Category = () => {
  const [data, setData] = useState([]);
  const [visibleItems, setVisibleItems] = useState(4);
  const history = useHistory();

  useEffect(() => {
    axios
      .get('https://fakestoreapi.com/products')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  const handleShowMore = () => {
    setVisibleItems((prevVisibleItems) => prevVisibleItems + 4);
  };

  const handleCategoryClick = (category) => {
    // Redirect to the category content page
    history.push(`/category-content/${category}`);
  };

  return (
    <div className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20">
      <h1 className="text-gray-800 text-4xl mb-6 font-bold text-center">Category</h1>
      <div className="grid gap-10 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-2 w-full">
        {data.slice(0, visibleItems).map((product, index) => (
          <div
            key={index}
            className="relative overflow-hidden transition duration-300 transform rounded shadow-lg lg:hover:-translate-y-2 hover:shadow-2xl h-[20rem]"
            onClick={() => handleCategoryClick(product.category)}
          >
            <img
              className="object-cover w-full h-[20rem]"
              src={product.image}
              alt={`Product Image ${index + 1}`}
            />
            <div className="absolute inset-0 flex flex-col justify-start px-5 py-4 text-start transition-opacity duration-300">
              <p className="font-bold text-white text-1xl">{product.category}</p>
            </div>
          </div>
        ))}
      </div>
      <button
        onClick={handleShowMore}
        className="p-2 bg-gray-800 text-white rounded-3xl mt-4 hover:bg-gray-600"
      >
        Show More
      </button>
    </div>
  );
};

export default Category;
