// Course.js

import React from 'react';

const Course = ({ course }) => {
  return (
    <div className="container mx-auto mt-8">
      <div className="max-w-md mx-auto bg-gray-100 p-8 rounded shadow-md">
        <h2 className="text-2xl font-bold mb-4">Course Details</h2>

        <div className="mb-4">
          <span className="block text-sm font-bold text-gray-600">Course Name:</span>
          <span className="block text-lg">{course.courseName}</span>
        </div>

        <div className="mb-4">
          <span className="block text-sm font-bold text-gray-600">Category:</span>
          <span className="block text-lg">{course.category}</span>
        </div>

        <div>
          <span className="block text-sm font-bold text-gray-600">Duration (in days):</span>
          <span className="block text-lg">{course.duration}</span>
        </div>
      </div>
    </div>
  );
};

export default Ab;
