import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import queryString from 'query-string';

const Pricing = () => {
  const { id } = useParams();
  const [pricingData, setPricingData] = useState([]);
  const [newPlan, setNewPlan] = useState({
    name: '',
    price: '',
    description: ''
  });

  useEffect(() => {
    fetchData();
  }, [id]);

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/getplansfortrainer/${id}`);
      const data = Array.isArray(response.data) ? response.data : [response.data];
      setPricingData(data);
    } catch (error) {
      console.error('Error fetching pricing data:', error);
    }
  };

  const handleBuyNow = (item) => {
    const queryParams = queryString.stringify({ name: item.name, price: item.price });
    window.location.href = `/payment?${queryParams}`;
  };

  const handleCreatePlan = async () => {
    try {
      await axios.post(`http://localhost:3000/Pricing/${id}`, newPlan);
      // After creating a new plan, refresh the data
      fetchData();
      // Clear the input fields
      setNewPlan({ name: '', price: '', description: '' });
    } catch (error) {
      console.error('Error creating pricing plan:', error);
    }
  };

  const handleUpdatePlan = async (item) => {
    try {
      await axios.post(`http://localhost:3000/Pricing/${item.id}`, item);
      // After updating the plan, refresh the data
      fetchData();
    } catch (error) {
      console.error('Error updating pricing plan:', error);
    }
  };

  const handleDeletePlan = async (item) => {
    try {
      await axios.post(`http://localhost:8080/softdeleteplan/${item.id}`);
      // After soft deleting the plan, refresh the data
      fetchData();
    } catch (error) {
      console.error('Error soft deleting pricing plan:', error);
    }
  };

  return (
    <div>
      <div className="min-w-screen min-h-screen bg-gray-200 px-5 py-5">
        <div className="w-full mx-auto bg-grey-100 px-5 py-10 text-gray-600 mb-10">
          <div className="text-center max-w-xl mx-auto">
            <h1 className="text-gray-800 text-6xl mb-6 font-bold item-center justify-center text-center">Pricing</h1>
            <h3 className="text-xl font-medium mb-10 text-gray-800">
              Lorem ipsum dolor sit amet consectetur adipisicing elit repellat
              dignissimos laboriosam odit accusamus porro
            </h3>
          </div>
          <div className="max-w-4xl mx-auto md:flex">
            {pricingData.slice(0, 3).map((item, index) => (
              <div key={index} className="w-full md:w-1/3 md:max-w-none bg-white px-8 md:px-10 py-8 md:py-10 mb-3 mx-auto md:my-6 rounded-md shadow-lg shadow-gray-600 md:flex md:flex-col">
                <div className="w-full flex-grow">
                  <h2 className="text-center font-bold uppercase mb-4 text-gray-800">{item.name}</h2>
                  <h3 className="text-center font-bold text-4xl mb-5 text-gray-800">{item.price}</h3>
                  <p className="text-sm px-5 mb-8">{item.description}</p>
                </div>
                <div className="w-full flex flex-col items-center">
                  <button onClick={() => handleBuyNow(item)} className="font-bold bg-gray-800 text-white rounded-3xl mt-4 hover:bg-[#89B9AD] px-10 py-2 transition-colors w-full">
                    Buy Now
                  </button>
                  <button onClick={() => handleUpdatePlan(item)} className="mt-2 text-gray-800 hover:text-gray-600 cursor-pointer">
                    Update Plan
                  </button>
                  <button onClick={() => handleDeletePlan(item)} className="mt-2 text-gray-800 hover:text-gray-600 cursor-pointer">
                    Delete Plan
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="max-w-xl mx-auto my-6">
        <h2 className="text-center font-bold uppercase mb-4 text-gray-800">Create New Plan</h2>
        <div className="mb-3">
          <input
            type="text"
            placeholder="Name"
            className="w-full px-4 py-3 border-2 placeholder:text-neutral-800 dark:text-white rounded-md outline-none dark:placeholder:text-neutral-200 dark:bg-neutral-900 focus:ring-4 border-neutral-300 focus:border-neutral-600 ring-neutral-100 dark:border-neutral-600 dark:focus:border-white dark:ring-0"
            value={newPlan.name}
            onChange={(e) => setNewPlan({ ...newPlan, name: e.target.value })}
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            placeholder="Price"
            className="w-full px-4 py-3 border-2 placeholder:text-neutral-800 dark:text-white rounded-md outline-none dark:placeholder:text-neutral-200 dark:bg-neutral-900 focus:ring-4 border-neutral-300 focus:border-neutral-600 ring-neutral-100 dark:border-neutral-600 dark:focus:border-white dark:ring-0"
            value={newPlan.price}
            onChange={(e) => setNewPlan({ ...newPlan, price: e.target.value })}
          />
        </div>
        <div className="mb-3">
          <textarea
            placeholder="Description"
            className="w-full px-4 py-3 border-2 placeholder:text-neutral-800 dark:text-white dark:placeholder:text-neutral-200 dark:bg-neutral-900 rounded-md outline-none h-36 focus:ring-4 border-neutral-300 focus:border-neutral-600 ring-neutral-100 dark:border-neutral-600 dark:focus:border-white dark:ring-0"
            value={newPlan.description}
            onChange={(e) => setNewPlan({ ...newPlan, description: e.target.value })}
          />
        </div>
        <button onClick={handleCreatePlan} className="font-bold bg-gray-800 text-white rounded-3xl mt-4 hover:bg-[#89B9AD] px-10 py-2 transition-colors w-full">
          Create Plan
        </button>
      </div>
    </div>
  );
};

export default Pricing;
