// BlogDetails.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

const BlogDetails = () => {
  const [blog, setBlog] = useState({});
  const { id } = useParams();

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/posts/${id}`);
        setBlog(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchBlog();
  }, [id]);

  return (
    <div className="bg-gray-100 min-h-screen">
      <div className="container mx-auto py-16">
        <div className="max-w-3xl mx-auto bg-white rounded-md overflow-hidden shadow-lg">
          <img
            src="https://i.ytimg.com/vi/gey73xiS8F4/maxresdefault.jpg"
            alt=""
            className="w-full h-64 object-cover"
          />
          <div className="p-8">
            <h1 className="text-3xl font-bold text-gray-800 mb-4">{blog.title}</h1>
            <p className="text-gray-700">{blog.body}</p>
          </div>
        </div>
        <Link to="/blogs" className="block mt-8 text-gray-600 hover:underline">
          &larr; Back to Blogs
        </Link>
      </div>
    </div>
  );
};

export default BlogDetails;
