import React, { useState, useEffect } from 'react';

const Class = () => {
  const [gifUrl, setGifUrl] = useState('');

  useEffect(() => {
    const fetchGif = async () => {
      try {
        // استبدال URL_ENDPOINT بعنوان الـ endpoint الخاص بالصورة GIF
        const response = await fetch('https://www.genemedics.com/exercise-api');
        const blob = await response.blob();

        // تحويل الـ Blob إلى Object URL
        const imageUrl = URL.createObjectURL(blob);

        // تحديث حالة الصورة
        setGifUrl(imageUrl);
      } catch (error) {
        console.error('Error fetching GIF:', error);
      }
    };

    // استدعاء الدالة عند تحميل المكون
    fetchGif();
  }, []); // فارغ يعني أنه سيتم تشغيلها مرة واحدة عند تحميل المكون

  return (
    <div>
      {gifUrl && <img src={gifUrl} alt="GIF" />}
    </div>
  );
};

export default Class;
