import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Category = () => {
  const [data, setData] = useState([]);
  const [visibleItems, setVisibleItems] = useState(4);

  useEffect(() => {
    AOS.init();
    axios
      .get('http://localhost:3000/category')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  return (
    <div data-aos="fade-up" className="container mx-auto px-4 py-12 md:py-16 lg:py-20">
      <h1 className="text-[#27374D] text-4xl mb-6 font-bold text-center">Explore Categories</h1>
      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
        {data.slice(0, visibleItems).map((item, index) => (
          <Link to={`/category/${item.id}`} key={index} className="block group relative overflow-hidden transition duration-300 transform rounded-lg hover:shadow-xl hover:-translate-y-2">
            <img
              className="object-cover w-full h-48 md:h-56 rounded-t-lg"
              src={item.image}
              alt={`Category Image ${index + 1}`}
            />
            <div className="p-4 bg-white rounded-b-lg">
              <p className="text-[#27374D] font-semibold text-lg">{item.category}</p>
              {/* Add additional elements if needed */}
            </div>
          </Link>
        ))}
      </div>
      <Link to="/CategoryItems" className="block mt-8 text-center">
        <button className="py-2 px-6 bg-[#27374D] text-white rounded-full hover:bg-blue-700">
          View All Categories
        </button>
      </Link>
    </div>
  );
};

export default Category;
