import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AOS from 'aos';
import 'aos/dist/aos.css';

const AchievementCard = ({ value, description }) => (
  <div className="bg-gradient-to-r from-[#FDE68A] to-[#FDBA74] px-6 py-8 md:px-10 md:py-12 rounded-lg shadow-md transition-transform transform hover:scale-105 ease-in-out duration-300">
    <div className="flex flex-col items-center justify-center">
      <p className="font-bold text-3xl sm:text-4xl lg:text-5xl leading-9 text-primary mb-2">{value}</p>
      <p className="font-medium text-base sm:text-lg leading-6 text-center text-gray-800">{description}</p>
    </div>
  </div>
);

const Statestic = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    AOS.init();
    // Make an Axios request to fetch data
    axios.get('http://localhost:3000/statestic')
      .then(response => {
        // Assuming your API response is an array of objects with 'value' and 'description' properties
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []); // The empty dependency array ensures that the effect runs only once when the component mounts

  return (
    <div>
      <section data-aos="fade-up" className="flex flex-col mt-20">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 place-items-center w-full mx-auto max-w-7xl px-5">
          {data.map((item, index) => (
            <AchievementCard key={index} value={item.value} description={item.description} />
          ))}
        </div>
      </section>
    </div>
  );
}

export default Statestic;
