import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TrainerDetails = () => {
  const { trainerId } = useParams();
  const [trainerDetails, setTrainerDetails] = useState(null);

  useEffect(() => {
    const fetchTrainerDetails = async () => {
      try {
        // Replace 'your-api-endpoint' with the actual API endpoint for fetching trainer details
        const response = await axios.get(`your-api-endpoint/trainers/${trainerId}`);
        setTrainerDetails(response.data);
      } catch (error) {
        console.error('Error fetching trainer details: ', error);
      }
    };

    fetchTrainerDetails();
  }, [trainerId]);

  // Render loading state while fetching data
  if (!trainerDetails) {
    return <p>Loading...</p>;
  }

  return (
    <div className="container mx-auto my-8 p-8 bg-white rounded-lg shadow-md">
      <h2 className="text-3xl font-bold mb-4">Trainer Details</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div>
          <img src={trainerDetails.image} alt={trainerDetails.name} className="w-full h-auto rounded-lg" />
        </div>
        <div>
          <p className="text-gray-600 mb-2">Trainer ID: {trainerId}</p>
          <h3 className="text-xl font-semibold mb-4">{trainerDetails.name}</h3>
          <p className="text-gray-800 mb-4">{trainerDetails.description}</p>
          {/* Additional details about the trainer */}
        </div>
      </div>
    </div>
  );
};

export default TrainerDetails;
