// TrainerDetails.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import Pricing from './Pricing';

const TrainerDetails = () => {
  const [trainer, setTrainer] = useState({});
  const { id } = useParams();

  useEffect(() => {
    const fetchTrainerDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/Card/${id}`);
        setTrainer(response.data);
      } catch (error) {
        console.error('Error fetching trainer details: ', error);
      }
    };

    fetchTrainerDetails();
  }, [id]);

  return (
    <div id="trainer-details" className="relative bg-white overflow-hidden mt-16">
      <div className="max-w-7xl mx-auto">
        <div className="relative z-10 pb-8 bg-white sm:pb-16 md:pb-20 lg:max-w-2xl lg:w-full lg:pb-28 xl:pb-32">
          <div className="pt-1" />
          <main className="mt-10 mx-auto max-w-7xl px-4 sm:mt-12 sm:px-6 md:mt-16 lg:mt-20 lg:px-8 xl:mt-28">
            <div className="sm:text-center lg:text-left">
              <img className="h-56 w-full object-cover object-top sm:h-72 md:h-96 lg:w-full lg:h-full mx-auto" src={trainer.image} alt={trainer.title} />
              <h2 className="my-6 text-2xl tracking-tight font-extrabold text-gray-900 sm:text-3xl md:text-4xl">
                Coach: {trainer.name}
              </h2>
              <p className="text-lg text-gray-700">{trainer.category}</p>
              <p className="text-lg text-gray-700">{trainer.title}</p>
              <p className="text-lg text-gray-700">{trainer.description}</p>
            </div>
          </main>
        </div>
      </div>
      <div className="lg:absolute lg:inset-y-0 lg:right-0 lg:w-1/2">
        <img
          className="h-56 w-full object-cover object-top sm:h-72 md:h-96 lg:w-full lg:h-full"
          src="https://cdn.pixabay.com/photo/2016/03/23/04/01/woman-1274056_960_720.jpg"
          alt=""
        />
      </div>
      <Pricing />
      <div className="absolute bottom-0 left-0 right-0 bg-gray-800 text-white text-center py-2">
        <Link
          to="/trainers"
          className="text-sm font-medium hover:underline"
        >
          &larr; Back to Trainers
        </Link>
      </div>
    </div>
  );
};

export default TrainerDetails;
