import React, { useState } from 'react';

const BestTrainers = () => {
  const trainers = [
    {
      name: 'Oliver Aguilerra',
      role: 'Product Manager',
      description: 'Vincent Van Gogh’s most popular painting, The Starry Night.',
      image: 'https://img.welt.de/img/iconist/partnerschaft/mobile237263579/4622508257-ci102l-w1024/dfae9494-88ba-4710-94cf-d774a0ec419b.jpg',
    },
    // Add more trainers as needed
  ];

  const [currentTrainer, setCurrentTrainer] = useState(0);

  const showNextTrainer = () => {
    setCurrentTrainer((prevIndex) => (prevIndex + 1) % trainers.length);
  };

  const showPreviousTrainer = () => {
    setCurrentTrainer((prevIndex) => (prevIndex - 1 + trainers.length) % trainers.length);
  };

  const trainer = trainers[currentTrainer];

  return (
    <div>
      <div className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20">
        <h1 className="text-gray-800 text-4xl mb-6 font-bold item-center justify-center text-center">Best Trainers</h1>
        <div className="grid gap-10 sm:grid-cols-2 lg:grid-cols-4">
          <div>
            <div className="relative overflow-hidden transition duration-300 transform rounded shadow-lg lg:hover:-translate-y-2 hover:shadow-2xl">
              <img
                className="object-cover w-full h-56 md:h-64 xl:h-80"
                src={trainer.image}
                alt="Person"
              />
              <div className="absolute inset-0 flex flex-col justify-center px-5 py-4 text-center transition-opacity duration-300 bg-black bg-opacity-75 opacity-0 hover:opacity-100">
                <p className="mb-1 text-lg font-bold text-gray-100">{trainer.name}</p>
                <p className="mb-4 text-xs text-gray-100">{trainer.role}</p>
                <p className="mb-4 text-xs tracking-wide text-gray-400">{trainer.description}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Next and Previous Arrows */}
        <div className="flex justify-center mt-6">
          <button
            onClick={showPreviousTrainer}
            className="mr-4 p-2 bg-gray-800 text-white rounded-full hover:bg-gray-700 focus:outline-none"
          >
            Previous
          </button>
          <button
            onClick={showNextTrainer}
            className="p-2 bg-gray-800 text-white rounded-full hover:bg-gray-700 focus:outline-none"
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default BestTrainers;
