// TrainerDetails.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import Pricing from './Pricing';

const TrainerDetails = () => {
  const [trainer, setTrainer] = useState({});
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    const fetchTrainerDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/Card/${id}`);
        setTrainer(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching trainer details: ', error);
        setLoading(false);
      }
    };

    fetchTrainerDetails();
  }, [id]);

  if (loading) {
    return <p>Loading...</p>; // You can replace this with a loading spinner or component
  }

  return (
    <div className="max-w-7xl mx-auto mt-16 bg-white overflow-hidden">
      <div className="lg:flex lg:items-center lg:justify-between lg:py-12">
        <div className="lg:w-1/2">
          <img className="h-56 w-full object-cover object-top sm:h-72 md:h-96 lg:w-full lg:h-full mx-auto" src={trainer.image} alt={`Image of ${trainer.name}`} />
        </div>
        <div className="max-w-2xl mt-6 mx-auto lg:mt-0 lg:max-w-3xl lg:w-1/2">
          <h2 className="text-3xl font-extrabold tracking-tight text-gray-900 sm:text-4xl">{trainer.name}</h2>
          <p className="mt-4 text-lg text-gray-700">{trainer.category}</p>
          <p className="mt-4 text-lg text-gray-700">{trainer.title}</p>
          <p className="mt-4 text-lg text-gray-700">{trainer.description}</p>
        </div>
      </div>
      <Pricing />
      <div className="bg-gray-800 text-white text-center py-2">
        <Link to="/trainers" className="text-sm font-medium hover:underline">
          &larr; Back to Trainers
        </Link>
      </div>
    </div>
  );
};

export default TrainerDetails;
