import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CourseDetails = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    // Replace 'your-api-endpoint-here' with the actual API endpoint
    axios.get('your-api-endpoint-here')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div>
      {data ? (
        <div className="flex items-start justify-start py-8 px-4">
          <div className="max-w-sm w-full shadow-lg">
            <div className="md:p-8 p-5 dark:bg-gray-800 bg-white rounded-t">
              <div className="px-4 flex items-center justify-between">
                <span
                  tabIndex={0}
                  className="focus:outline-none  text-base font-bold dark:text-gray-100 text-gray-800"
                >
                  October 2020
                </span>
                <div className="flex items-center">
                  <button
                    aria-label="calendar backward"
                    className="focus:text-gray-400 hover:text-gray-400 text-gray-800 dark:text-gray-100"
                  >
                    {/* ... Icon code goes here */}
                  </button>
                  <button
                    aria-label="calendar forward"
                    className="focus:text-gray-400 hover:text-gray-400 ml-3 text-gray-800 dark:text-gray-100"
                  >
                    {/* ... Icon code goes here */}
                  </button>
                </div>
              </div>
              {/* ... Rest of the code for the calendar */}
            </div>
            <div className="md:py-8 py-5 md:px-16 px-5 dark:bg-gray-700 bg-gray-50 rounded-b">
              <div className="px-4">
                {data.events.map((event, index) => (
                  <div key={index} className="border-b pb-4 border-gray-400 border-dashed">
                    <p className="text-xs font-light leading-3 text-gray-500 dark:text-gray-300">
                      {event.time}
                    </p>
                    <a
                      tabIndex={0}
                      className="focus:outline-none text-lg font-medium leading-5 text-gray-800 dark:text-gray-100 mt-2"
                    >
                      {event.title}
                    </a>
                    <p className="text-sm pt-2 leading-4 leading-none text-gray-600 dark:text-gray-300">
                      {event.description}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default CourseDetails;
