import React from 'react';
import Users from './Users';
import Blogs from './Blogs';
import Courses from './Courses';
import ContactUs from './Contactus';

const StatCard = ({ icon, value, label }) => (
  <div className="p-4 md:w-1/4 sm:w-1/2 w-full">
    <div className="border-2 border-gray-600 px-4 py-6 rounded-lg transform transition duration-500 hover:scale-110">
      <svg
        fill="none"
        stroke="currentColor"
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        className="text-indigo-500 w-12 h-12 mb-3 inline-block"
        viewBox="0 0 24 24"
      >
        {/* Icon */}
        {getIcon(icon)}
      </svg>
      <h2 className="title-font font-medium text-3xl text-gray-900">{value}</h2>
      <p className="leading-relaxed">{label}</p>
    </div>
  </div>
);

const getIcon = (icon) => {
  switch (icon) {
    case 'downloads':
      return (
        <>
          <path d="M8 17l4 4 4-4m-4-5v9" />
          <path d="M20.88 18.09A5 5 0 0018 9h-1.26A8 8 0 103 16.29" />
        </>
      );
    case 'users':
      return (
        <>
          <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
          <circle cx={9} cy={7} r={4} />
          <path d="M23 21v-2a4 4 0 00-3-3.87m-4-12a4 4 0 010 7.75" />
        </>
      );
    case 'files':
      return (
        <>
          <path d="M3 18v-6a9 9 0 0118 0v6" />
          <path d="M21 19a2 2 0 01-2 2h-1a2 2 0 01-2-2v-3a2 2 0 012-2h3zM3 19a2 2 0 002 2h1a2 2 0 002-2v-3a2 2 0 00-2-2H3z" />
        </>
      );
    default:
      return null;
  }
};

const Dashboard = () => (
  <div>
    <div className="text-gray-700  body-font flex justify-center items-center ml-96 ">
      <div className="container">
        <div className="flex flex-wrap  text-center">
          {/* Statistical Cards */}
          <StatCard icon="downloads" value="2.7K" label="Downloads" />
          <StatCard icon="users" value="1.3K" label="Users" />
          <StatCard icon="files" value="74" label="Files" />
        </div>
      </div>
      {/* Additional Components */}

      <Users />
      <Blogs />
      <Courses />
      <ContactUs />
    </div>
  </div>
);

export default Dashboard;
