// في ملف Category.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AOS from "aos";
import "aos/dist/aos.css";
import CategoryItems from './CategoryItems'; // استيراد مكون العناصر

const Category = () => {
  const [data, setData] = useState([]);
  const [staticTitles, setStaticTitles] = useState([
    'fitness',
    'CROSSFIT',
    'CARDIO',
    'BODY BUILDING',
    'kickboxing',
  ]);

  useEffect(() => {
    AOS.init();
    axios
      .get("http://localhost:3000/Card")
      .then((response) => {
        setData(response.data); // تحديث البيانات عند الاستجابة من الخادم
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, []);

  return (
    <div data-aos="fade-up" className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20 ">
      <h1 className="text-[#27374D] text-4xl mb-6 font-bold item-center justify-center text-center">Category</h1>
      <div>
        <Link to="/category-items">
          <button className="p-2 bg-gray-800 text-white rounded-xl mt-2 hover:bg-gray-600">
            Show More
          </button>
        </Link>
      </div>
    </div>
  );
};

export default Category;
