import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseDetails = () => {
  const [nutritionData, setNutritionData] = useState(null);
  const [workoutData, setWorkoutData] = useState(null);

  useEffect(() => {
    const fetchNutritionData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/Nutrition');
        setNutritionData(response.data);
      } catch (error) {
        console.error('Error fetching nutrition data:', error);
      }
    };

    const fetchWorkoutData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/Workout');
        setWorkoutData(response.data);
      } catch (error) {
        console.error('Error fetching workout data:', error);
      }
    };

    fetchNutritionData();
    fetchWorkoutData();
  }, []); // The empty dependency array ensures that the effect runs only once after the component mounts

  return (
    <div className="flex">
      <div className="bg-white rounded shadow-md max-w-xl mx-auto p-4 mt-4 ml-4">
        <div className="text-2xl font-bold">Bodybuilder's Nutrition Facts</div>
        <table className="table w-full border border-black border-collapse">
          <thead>
            <tr className="border-b border-black">
              <th className="py-2 font-medium border-r border-black">Nutrient</th>
              <th className="py-2 border-r border-black">Amount</th>
              <th className="py-2 border-l border-black">% Daily Value</th>
            </tr>
          </thead>
          <tbody>
            {nutritionData &&
              nutritionData.map((item, index) => (
                <tr key={index} className="border-b border-black">
                  <td className="py-2 font-medium border-r border-black">{item.nutrient}</td>
                  <td className="py-2 border-r border-black">{item.amount}</td>
                  <td className="py-2 border-l border-black">{item.dailyValue}</td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>

      <div className="bg-white rounded shadow-md max-w-xl mx-auto p-4 mt-24 ml-auto mr-0">
        <div className="text-2xl font-bold">Workout Routine</div>
        <table className="table w-full border border-black border-collapse">
          <thead>
            <tr className="border-b border-black">
              <th className="py-2 font-medium border-r border-black">Exercise</th>
              <th className="py-2 border-r border-black">Repetitions</th>
              <th className="py-2 border-l border-black">Rest</th>
            </tr>
          </thead>
          <tbody>
            {workoutData &&
              workoutData.map((exercise, index) => (
                <tr key={index} className="border-b border-black">
                  <td className="py-2 font-medium border-r border-black">{exercise.name}</td>
                  <td className="py-2 border-r border-black">{exercise.repetitions}</td>
                  <td className="py-2 border-l border-black">{exercise.rest}</td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CourseDetails;
