import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const blogsPerPage = 6;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("https://jsonplaceholder.typicode.com/posts");
        setBlogs(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchData();
  }, []); 

  const indexOfLastBlog = currentPage * blogsPerPage;
  const indexOfFirstBlog = indexOfLastBlog - blogsPerPage;
  const currentBlogs = blogs.slice(indexOfFirstBlog, indexOfLastBlog);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const nextPage = () => setCurrentPage(currentPage + 1);
  const prevPage = () => setCurrentPage(currentPage - 1);

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-3 gap-6 mx-[9rem] my-4 ">
      {currentBlogs.map((blog) => (
        <div key={blog.id} className="bg-white rounded-md overflow-hidden shadow-lg">
          {/* ... your existing code ... */}
        </div>
      ))}

      <div className="col-span-full flex justify-center mt-4">
        <button
          onClick={prevPage}
          className="mx-2 p-2 bg-gray-800 text-white hover:bg-gray-700 focus:outline-none"
          style={{ width: "40px", height: "40px", borderRadius: "50%" }}
          disabled={currentPage === 1}
        >
          {"<"}
        </button>
        {Array.from({ length: Math.ceil(blogs.length / blogsPerPage) }, (_, index) => index + 1).map((number) => (
          <button
            key={number}
            onClick={() => paginate(number)}
            className={`mx-2 p-2 bg-gray-800 text-white hover:bg-gray-700 focus:outline-none ${
              currentPage === number ? "bg-gray-800" : ""
            }`}
            style={{ width: "40px", height: "40px", borderRadius: "50%" }}
          >
            {number}
          </button>
        ))}
        <button
          onClick={nextPage}
          className="mx-2 p-2 bg-gray-800 text-white hover:bg-gray-700 focus:outline-none"
          style={{ width: "40px", height: "40px", borderRadius: "50%" }}
          disabled={currentPage === Math.ceil(blogs.length / blogsPerPage)}
        >
          {">"}
        </button>
      </div>
    </div>
  );
};

export default Blogs;
