import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';

const BestTrainers = () => {
  const [trainers, setTrainers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTrainers = async () => {
      AOS.init();
      try {
        const response = await axios.get('http://localhost:8080/getAllTrainers');
        setTrainers(response.data);
        setError(null); // Clear any previous errors
      } catch (error) {
        console.error('Error fetching trainers: ', error);
        setError('Error fetching trainers. Please check the console for details.');
      }
    };

    fetchTrainers();
  }, []);

  return (
    <div data-aos="fade-up" className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20 mt-24">
      <h1 className="text-[#27374D] text-4xl mb-6 font-bold text-center">Best Trainers</h1>
      {error ? (
        <p className="text-red-500">{error}</p>
      ) : (
        <div className="grid gap-10 sm:grid-cols-2 lg:grid-cols-4">
          {trainers.map((trainer) => (
            <div key={trainer.trainer_id} className="group">
              <Link to={`/trainers/${trainer.trainer_id}`}>
                <div className="relative overflow-hidden rounded-lg shadow-xl border-2 border-[#1A202C] transition duration-300 transform hover:-translate-y-2 hover:shadow-2xl">
                  {/* Rest of your component */}
                </div>
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BestTrainers;
