import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Category = () => {
  const [data, setData] = useState([]);
  const [visibleItems, setVisibleItems] = useState(4);

  useEffect(() => {
    AOS.init();
    axios
      .get('http://localhost:3000/category')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  return (
    <div data-aos="fade-up" className="container mx-auto px-4 py-16 sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20">
      <h1 className="text-[#27374D] text-4xl mb-6 font-bold text-center">Category</h1>
      <div className="grid gap-10 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-2">
        {data.slice(0, visibleItems).map((item, index) => (
          <Link to={`/category/${item.id}`} key={index} className="group block overflow-hidden transition duration-300 transform rounded shadow-lg hover:shadow-2xl hover:-translate-y-2">
            <img
              className="object-cover w-full h-64 rounded-t-md"
              src={item.image}
              alt={`Category Image ${index + 1}`}
            />
            <div className="relative inset-0 flex flex-col items-center justify-center absolute bg-gradient-to-t from-black to-transparent group-hover:opacity-100 opacity-0 transition-opacity duration-300">
              <p className="font-bold text-white text-2xl mb-2">{item.category}</p>
              {/* Add additional elements if needed */}
            </div>
          </Link>
        ))}
      </div>
      <Link to="/CategoryItems" className="block mt-6">
        <button className="py-2 px-4 bg-gray-800 text-white rounded-xl hover:bg-gray-600">
          Show More
        </button>
      </Link>
    </div>
  );
};

export default Category;
