// TrainerDetails.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import Pricing from './Pricing';

const TrainerDetails = () => {
  const [trainer, setTrainer] = useState({});
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    const fetchTrainerDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/Card/${id}`);
        setTrainer(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching trainer details: ', error);
        setLoading(false);
      }
    };

    fetchTrainerDetails();
  }, [id]);

  if (loading) {
    return <p>Loading...</p>; // You can replace this with a loading spinner or component
  }

  return (
    <div className="max-w-md mx-auto bg-white rounded-xl shadow-md overflow-hidden w-6">
      <div className="md:flex">
        <div className="md:shrink-0">
          <img
            className="h-48 w-full object-cover md:h-full md:w-48"
            src={trainer.image}
            alt={`Image of ${trainer.name}`}
          />
        </div>
        <div className="p-8">
          <div className="uppercase tracking-wide text-sm text-indigo-500 font-semibold">
            {trainer.category}
          </div>
          <h2 className="block mt-1 text-lg leading-tight font-medium text-black hover:underline">
            {trainer.name}
          </h2>
          <p className="mt-2 text-gray-700">
            {trainer.description}
          </p>
        </div>
      </div>
      <Pricing />
      <div className="bg-gray-800 text-white text-center py-2">
        <Link to="/trainers" className="text-sm font-medium hover:underline">
          &larr; Back to Trainers
        </Link>
      </div>
    </div>
  );
};

export default TrainerDetails;
