import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Blogs = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get('https://example.com/api/articles');
        setArticles(response.data);
      } catch (error) {
        console.error('Error fetching articles:', error);
      }
    };

    fetchArticles();
  }, []);

  return (
    <div>
      <section className="pt-20 pb-10 lg:pt-[120px] lg:pb-20 bg-white dark:bg-dark mx-20">
        <div className="container mx-auto">
          <div className="flex flex-wrap justify-center -mx-4">
            <div className="w-full px-4">
              <div className="mx-auto mb-[60px] max-w-[510px] text-center lg:mb-20">
                <span className="block mb-2 text-lg font-semibold text-primary">
                  Our Blogs
                </span>
                <h2 className="mb-4 text-3xl font-bold text-dark sm:text-4xl md:text-[40px] dark:text-white">
                  Our Recent News
                </h2>
                <p className="text-base text-body-color dark:text-dark-6">
                  There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form.
                </p>
              </div>
            </div>
          </div>
          <div className="flex flex-wrap -mx-4">
            {articles.map((article) => (
              <div key={article.id} className="w-full px-4 md:w-1/2 lg:w-1/3">
                <div className="w-full mb-10">
                  <div className="mb-8 overflow-hidden rounded">
                    <img
                      src={article.image}
                      alt="image"
                      className="w-full"
                    />
                  </div>
                  <div>
                    <span className="inline-block px-4 py-1 mb-5 text-xs font-semibold leading-loose text-center text-black rounded bg-primary">
                      {article.date}
                    </span>
                    <h3>
                      <a
                        href={article.link}
                        className="inline-block mb-4 text-xl font-semibold text-dark dark:text-white hover:text-primary sm:text-2xl lg:text-xl xl:text-2xl"
                      >
                        {article.title}
                      </a>
                    </h3>
                    <p className="text-base text-body-color dark:text-dark-6">
                      {article.content}
                    </p>
                  </div>
                  <a
                    href={article.link}
                    className="right-[25%] left-[25%] w-40 inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white rounded-lg bg-primary hover:bg-primary-dark focus:ring-4 focus:outline-none"
                  >
                    Read more
                    <svg
                      className="w-3.5 h-3.5 ml-2"
                      aria-hidden="true"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 14 10"
                    >
                      <path
                        stroke="currentColor"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M1 5h12m0 0L9 1m4 4L9 9"
                      />
                    </svg>
                  </a>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Blogs;
