import React from "react";

const BlogDetails = ({ blog }) => {
  return (
    <div className="max-w-screen-xl mx-auto p-5 sm:p-10 md:p-16 relative">
      <div
        className="bg-cover bg-center text-center overflow-hidden"
        style={{
          minHeight: 500,
          backgroundImage: `url("${blog.coverImage}")`,
        }}
        title={blog.title}
      ></div>
      <div className="max-w-3xl mx-auto">
        <div className="mt-3 bg-white rounded-b lg:rounded-b-none lg:rounded-r flex flex-col justify-between leading-normal">
          <div className="bg-white relative top-0 -mt-32 p-5 sm:p-10">
            <h1 className="text-gray-900 font-bold text-3xl mb-2">{blog.title}</h1>
            <p className="text-gray-700 text-xs mt-2">
              Written By:{" "}
              <a
                href="#"
                className="text-indigo-600 font-medium hover:text-gray-900 transition duration-500 ease-in-out"
              >
                {blog.author}
              </a>{" "}
              In{" "}
              {blog.categories.map((category, index) => (
                <React.Fragment key={category}>
                  <a
                    href="#"
                    className="text-xs text-indigo-600 font-medium hover:text-gray-900 transition duration-500 ease-in-out"
                  >
                    {category}
                  </a>
                  {index !== blog.categories.length - 1 && ", "}
                </React.Fragment>
              ))}
            </p>
            <p className="text-base leading-8 my-5">{blog.content}</p>
            {blog.subheadings &&
              blog.subheadings.map((subheading, index) => (
                <React.Fragment key={index}>
                  <h3 className="text-2xl font-bold my-5">{subheading.title}</h3>
                  <p className="text-base leading-8 my-5">{subheading.content}</p>
                </React.Fragment>
              ))}
            {blog.blockquote && (
              <blockquote className="border-l-4 text-base italic leading-8 my-5 p-5 text-indigo-600">
                {blog.blockquote}
              </blockquote>
            )}
            {blog.tags &&
              blog.tags.map((tag, index) => (
                <React.Fragment key={tag}>
                  <a
                    href="#"
                    className="text-xs text-indigo-600 font-medium hover:text-gray-900 transition duration-500 ease-in-out"
                  >
                    {tag}
                  </a>
                  {index !== blog.tags.length - 1 && ", "}
                </React.Fragment>
              ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BlogDetails;
