// TrainerDetails.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TrainerDetails = () => {
  const [trainer, setTrainer] = useState({});
  const { id } = useParams();

  useEffect(() => {
    const fetchTrainerDetails = async () => {
      try {
        const response = await axios.get(`https://fakestoreapi.com/products/${id}`);
        setTrainer(response.data);
      } catch (error) {
        console.error('Error fetching trainer details: ', error);
      }
    };

    fetchTrainerDetails();
  }, [id]);

  return (
    <div className="container mx-auto p-4">
      <div className="max-w-2xl mx-auto bg-white shadow-md rounded-md overflow-hidden">
        <img
          className="w-full h-64 object-cover"
          src={trainer.image}
          alt={trainer.title}
        />
        <div className="p-6">
          <h2 className="text-2xl font-bold text-gray-800 mb-2">{trainer.title}</h2>
          <p className="text-sm text-gray-600 mb-4">{trainer.category}</p>
          <p className="text-base text-gray-700">{trainer.description}</p>
        </div>
      </div>
    </div>
  );
};

export default TrainerDetails;
