import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";

const TrainerPage = () => {
  const [trainers, setTrainers] = useState([]);
  const [filteredTrainers, setFilteredTrainers] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const category = useParams();
  const [currentPage, setCurrentPage] = useState(1);
  const trainersPerPage = 9;
  const [trainerImages, setTrainerImages] = useState(null);

  // Fetch trainers
  useEffect(() => {
    axios
      .get(`http://localhost:5000/Get_Trainers_By_Category/${category.category}`)
      .then((response) => {
        setTrainers(response.data.data);
        setTrainerImages(response.data.data[0].images);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, [category]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const searchFilteredTrainers = trainers.filter((trainer) =>
    trainer.trainer_name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const indexOfLastTrainer = currentPage * trainersPerPage;
  const indexOfFirstTrainer = indexOfLastTrainer - trainersPerPage;
  const currentTrainers = searchFilteredTrainers.slice(
    indexOfFirstTrainer,
    indexOfLastTrainer
  );

  const totalPages = Math.ceil(searchFilteredTrainers.length / trainersPerPage);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="my-16">
      <div className="flex justify-center items-center my-8">
        <div className="relative">
          <input
            type="text"
            className="px-4 py-2 border rounded-lg w-80 focus:outline-none focus:ring-2 focus:ring-blue-300"
            placeholder="Search for trainers"
            value={searchQuery}
            onChange={handleSearchChange}
          />
        </div>
      </div>

      <div className="relative flex flex-wrap gap-7 justify-center items-center mx-16">
        {currentTrainers.map((trainer) => (
          <div
            key={trainer.trainer_id}
            className="group my-2 flex w-full max-w-xs flex-col overflow-hidden rounded-lg border border-gray-100 bg-white shadow-md"
          >
            <Link
              className="relative mx-3 mt-3 flex h-60 overflow-hidden rounded-xl"
              to={`/trainer/${trainer.trainer_id}`}
            >
              <img
                className="peer absolute border top-0 right-0 h-full w-full object-cover"
                src={trainerImages[0].trainer_img}
                alt="trainer image"
              />
            </Link>
            <div className="mt-4 px-5 pb-5">
              <a href="#">
                <h5 className="text-xl text-start h-8 mb-5 overflow-hidden tracking-tight text-slate-900">
                  {trainer.trainer_name}
                </h5>
              </a>
              <p className="mb-4 text-xs text-gray-100">{trainer.trainer_role}</p>
              <p className="mb-4 text-xs tracking-wide text-gray-400">
                {trainer.trainer_description}
              </p>
            </div>
          </div>
        ))}
      </div>
      <div className="flex justify-center">
        <div className="flex justify-center mt-4">
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 1}
            className="mr-2 px-4 py-2 border text-black rounded-lg shadow"
          >
            Previous Page
          </button>
          {[...Array(totalPages)].map((_, index) => (
            <button
              key={index + 1}
              onClick={() => handlePageChange(index + 1)}
              className={`${
                currentPage === index + 1
                  ? "bg-teal-800 w-10 font-bold text-white"
                  : "bg-teal-600 w-10 text-white"
              } py-2 px-3 focus:outline-none rounded-lg mx-1`}
            >
              {index + 1}
            </button>
          ))}
          <button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage === totalPages}
            className="px-4 py-2 border text-black rounded-lg shadow"
          >
            Next Page
          </button>
        </div>
      </div>
    </div>
  );
};

export default BestSellery;
