import React, { useState } from 'react';

const BestSellery = () => {
  const [currentSlide, setCurrentSlide] = useState(1);

  const changeSlide = (newSlide) => {
    setCurrentSlide(newSlide);
  };

  return (
    <div className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20">
      <h1 className="text-gray-800 text-4xl mb-6 font-bold item-center justify-center text-center">Beast Trainers</h1>
      <div className="grid gap-10 sm:grid-cols-2 lg:grid-cols-4 relative">
        {/* Your existing slides */}

        {/* Previous button */}
        <button
          type="button"
          className={`${
            currentSlide !== 1 ? `text-white` : `text-black`
          } absolute top-1/2 left-0 transform -translate-y-1/2 mx-6 p-2 md:p-4`}
          onClick={() =>
            changeSlide(currentSlide === 1 ? 4 : currentSlide - 1)
          }
        >
          <span className="flex items-center">
            <svg
              aria-hidden="true"
              className="w-8 h-8 md:w-16 md:h-16"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M15 19l-7-7 7-7"
              ></path>
            </svg>
          </span>
        </button>

        {/* Next button */}
        <button
          type="button"
          className={`${
            currentSlide !== 1 ? `text-white` : `text-black`
          } absolute top-1/2 right-0 transform -translate-y-1/2 mx-6 p-2 md:p-4`}
          onClick={() =>
            changeSlide(currentSlide === 4 ? 1 : currentSlide + 1)
          }
        >
          <span className="flex items-center">
            <svg
              aria-hidden="true"
              className="w-8 h-8 md:w-16 md:h-16"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M9 5l7 7-7 7"
              ></path>
            </svg>
          </span>
        </button>
      </div>
    </div>
  );
};

export default BestSellery;
