import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCookies } from "react-cookie";
import { useNavigate } from "react-router-dom";
import Contactus from "../admin1/Contactus";
import Courses from "../admin1/Courses";
import Blogs from "../admin1/Blogs";
import Users from "../admin1/Users";
import Dashboard from "../admin1/Dashboard";

const Account = () => {
  const [user, setUser] = useState([]);
  const [page, setPage] = useState("dashboard");
  const [photoPreview, setPhotoPreview] = useState(null);
  const [cookies, setCookie, removeCookie] = useCookies(['token']);
  const history = useNavigate();

  // Fetch user data
  useEffect(() => {
    axios
      .get("http://localhost:5000/user")
      .then((response) => {
        setUser(response.data);
        setPhotoPreview(response.data.profile_image_name);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });
  }, []);

  // Logout function
  function logout() {
    removeCookie('token');
    history("/");
  }

  // Toggle sidebar
  const [isSideOpen, setIsSideOpen] = useState(true);
  const position = isSideOpen ? "left-0" : "-left-64";

  function toggleSidebar() {
    setIsSideOpen(!isSideOpen);
  }

  return (
    <div className="flex h-full">
      {/* Sidebar */}
      <div className={`w-64 h-full bg-gray-900 text-white transition-all duration-300 ${position}`}>
        {/* Sidebar Content */}
        <div className="flex flex-col h-full p-5 space-y-6">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-semibold">Dashboard</h1>
            <button
              aria-label="Toggle sidebar"
              className="lg:hidden p-2 focus:outline-none"
              onClick={toggleSidebar}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6 text-white"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path d="M4 6h16M4 12h16m-7 6h7"></path>
              </svg>
            </button>
          </div>
          {/* Sidebar navigation links */}
          <nav className="flex-1 space-y-4">
            <button className="w-full btn-sidebar" onClick={() => setPage("dashboard")}>
              Dashboard
            </button>
            <button className="w-full btn-sidebar" onClick={() => setPage("users")}>
              Users
            </button>
            <button className="w-full btn-sidebar" onClick={() => setPage("blogs")}>
              Blogs
            </button>
            <button className="w-full btn-sidebar" onClick={() => setPage("courses")}>
              Courses
            </button>
            <button className="w-full btn-sidebar" onClick={() => setPage("contactus")}>
              Contact Us
            </button>
          </nav>
          {/* Logout button */}
          <button
            className="w-full p-2 text-center bg-red-600 hover:bg-red-700 rounded-md"
            onClick={logout}
          >
            Logout
          </button>
        </div>
      </div>

      {/* Content */}
      <div className="flex-1 p-5">
        {/* Display selected page */}
        {page === "dashboard" && <Dashboard />}
        {page === "users" && <Users />}
        {page === "blogs" && <Blogs />}
        {page === "courses" && <Courses />}
        {page === "contactus" && <Contactus />}
      </div>
    </div>
  );
};

export default Account;
