import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Category = () => {
  const [data, setData] = useState([]);
  const [visibleItems, setVisibleItems] = useState(4);

  useEffect(() => {
    AOS.init();
    axios
      .get('http://localhost:3000/category')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  const handleVisibleItems = () => {
    // Adjust the number of visible items based on the screen size
    const screenWidth = window.innerWidth;

    if (screenWidth < 640) {
      setVisibleItems(1);
    } else if (screenWidth < 1024) {
      setVisibleItems(2);
    } else {
      setVisibleItems(4);
    }
  };

  useEffect(() => {
    // Update visible items on window resize
    window.addEventListener('resize', handleVisibleItems);
    handleVisibleItems(); // Initial calculation
    return () => {
      window.removeEventListener('resize', handleVisibleItems);
    };
  }, []);

  return (
    <div data-aos="fade-up" className="px-4 py-8 sm:py-12 md:py-16 lg:py-20 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8">
      <h1 className="text-[#22092C] text-2xl sm:text-3xl md:text-4xl lg:text-5xl mb-6 font-bold text-center">Explore Categories</h1>
      <div className="grid gap-6 sm:gap-8 md:gap-10 lg:gap-12 sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
        {data.slice(0, visibleItems).map((item, index) => (
          <Link to={`/category/${item.id}`} key={index}>
            {/* Your existing card component */}
          </Link>
        ))}
      </div>
      <Link to="/CategoryItems">
        <button className="p-3 bg-gradient-to-r from-[#22092C] to-[#363636] text-white rounded-xl mt-6 hover:opacity-80 transition-all duration-300 block mx-auto">
          Explore More Categories
        </button>
      </Link>
    </div>
  );
};

export default Category;
