// CourseDetails.js
import React from "react";
import { useParams } from "react-router-dom";

const CourseDetails = () => {
  const { courseId } = useParams();

  // Fetch course details based on the courseId (similar to how you fetched courses in Course.js)
  const courseDetails = {}; // Fetch or use the course details here

  // Dummy data for chat and training plan
  const chatMessages = []; // Fetch or use chat messages
  const trainingPlan = []; // Fetch or use training plan data

  return (
    <div>
      <h1>Course Details</h1>
      <h2>{courseDetails.Course_Name}</h2>
      <p>{courseDetails.Course_description}</p>

      {/* Chat Section */}
      <div>
      <>
  <div className="flex-1 p-2 sm:p-6 justify-between flex flex-col h-screen">
    {/* Header Section */}
    <div className="flex sm:items-center justify-between py-3 border-b-2 border-gray-200">
      {/* User Profile */}
      <div className="relative flex items-center space-x-4">
        <div className="relative">
          <span className="absolute text-green-500 right-0 bottom-0">
            <svg width={20} height={20}>
              <circle cx={8} cy={8} r={8} fill="currentColor" />
            </svg>
          </span>
          <img
            src="https://images.unsplash.com/photo-1549078642-b2ba4bda0cdb?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=3&w=144&h=144"
            alt=""
            className="w-10 sm:w-16 h-10 sm:h-16 rounded-full"
          />
        </div>
        {/* User Details */}
        <div className="flex flex-col leading-tight">
          <div className="text-2xl mt-1 flex items-center">
            <span className="text-gray-700 mr-3">Anderson Vanhron</span>
          </div>
          <span className="text-lg text-gray-600">Junior Developer</span>
        </div>
      </div>
      {/* Action Buttons */}
      <div className="flex items-center space-x-2">
        <button
          type="button"
          className="inline-flex items-center justify-center rounded-lg border h-10 w-10 transition duration-500 ease-in-out text-gray-500 hover:bg-gray-300 focus:outline-none"
        >
          {/* Add your SVG or icon for the button */}
        </button>
        {/* Add more buttons with different actions */}
      </div>
    </div>

    {/* Chat Messages Section */}
    <div
      id="messages"
      className="flex flex-col space-y-4 p-3 overflow-y-auto scrollbar-thumb-blue scrollbar-thumb-rounded scrollbar-track-blue-lighter scrollbar-w-2 scrolling-touch"
    >
      {/* Chat messages go here */}
      {/* You can map through your chat data and render each message */}
    </div>

    {/* Input and Send Message Section */}
    <div className="border-t-2 border-gray-200 px-4 pt-4 mb-2 sm:mb-0">
      <div className="relative flex">
        {/* Attach File Button */}
        <span className="absolute inset-y-0 flex items-center">
          <button
            type="button"
            className="inline-flex items-center justify-center rounded-full h-12 w-12 transition duration-500 ease-in-out text-gray-500 hover:bg-gray-300 focus:outline-none"
          >
            {/* Add your SVG or icon for the button */}
          </button>
        </span>
        {/* Input Field */}
        <input
          type="text"
          placeholder="Write your message!"
          className="w-full focus:outline-none focus:placeholder-gray-400 text-gray-600 placeholder-gray-600 pl-12 bg-gray-200 rounded-md py-3"
        />
        {/* Send Message Buttons */}
        <div className="absolute right-0 items-center inset-y-0 hidden sm:flex">
          {/* Add more buttons for different actions */}
          <button
            type="button"
            className="inline-flex items-center justify-center rounded-full h-10 w-10 transition duration-500 ease-in-out text-gray-500 hover:bg-gray-300 focus:outline-none"
          >
            {/* Add your SVG or icon for the button */}
          </button>
          {/* Add more buttons for different actions */}
          <button
            type="button"
            className="inline-flex items-center justify-center rounded-lg px-4 py-3 transition duration-500 ease-in-out text-white bg-blue-500 hover:bg-blue-400 focus:outline-none"
          >
            <span className="font-bold">Send</span>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              className="h-6 w-6 ml-2 transform rotate-90"
            >
              {/* Add your SVG path for the send button */}
            </svg>
          </button>
        </div>
      </div>
    </div>
  </div>

  {/* Custom Styles for Scrollbar */}
  <style
    dangerouslySetInnerHTML={{
      __html:
        "\n.scrollbar-w-2::-webkit-scrollbar {\n  width: 0.25rem;\n  height: 0.25rem;\n}\n\n.scrollbar-track-blue-lighter::-webkit-scrollbar-track {\n  --bg-opacity: 1;\n  background-color: #f7fafc;\n  background-color: rgba(247, 250, 252, var(--bg-opacity));\n}\n\n.scrollbar-thumb-blue::-webkit-scrollbar-thumb {\n  --bg-opacity: 1;\n  background-color: #edf2f7;\n  background-color: rgba(237, 242, 247, var(--bg-opacity));\n}\n\n.scrollbar-thumb-rounded::-webkit-scrollbar-thumb {\n  border-radius: 0.25rem;\n}\n"
    }}
  />
</>

      </div>

      {/* Training Plan Section */}
      <div>
        <h3>Training Plan</h3>
        <ul>
          {trainingPlan.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default CourseDetails;
