import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

const BlogDetails = () => {
  const [blog, setBlog] = useState({});
  const [relatedPosts, setRelatedPosts] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/blog/${id}`);
        setBlog(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchBlog();
  }, [id]);

  useEffect(() => {
    const fetchRelatedPosts = async () => {
      try {
        const response = await axios.get("http://localhost:3000/blog");
        const filteredRelatedPosts = response.data.slice(-3);
        setRelatedPosts(filteredRelatedPosts);
      } catch (error) {
        console.error("Error fetching related posts: ", error);
      }
    };

    fetchRelatedPosts();
  }, []);

  return (
    <div className="mt-8 bg-gray-50">
      <div className="px-4 md:px-10 py-6 mx-auto max-w-6xl flex flex-col md:flex-row">
        <div className="md:w-1/2 md:pr-8">
          <img
            src={blog.blog_img}
            alt=""
            className="w-full h-60 object-cover mb-4 md:mb-0 rounded-md shadow-lg"
          />
        </div>
        <div className="md:w-1/2">
          <div className="max-w-4xl mx-auto bg-white rounded-md overflow-hidden shadow-lg p-8">
            <h1 className="text-3xl md:text-4xl lg:text-5xl font-bold text-purple-500 mb-4">
              {blog.blog_title}
            </h1>
            <div className="font-light text-gray-600 mb-6">
              <span className="flex items-center">
                <svg
                  className="w-4 h-4 mr-2"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8"
                  ></path>
                </svg>
                {blog.author}
              </span>
            </div>
            <div className="max-w-4xl mx-auto text-lg text-gray-700 mt-4 rounded bg-gray-100">
              <p className="mt-2 leading-relaxed">{blog.blog_description}</p>
            </div>
          </div>
        </div>
      </div>
      <div className="px-4 md:px-10 mt-8">
        <h2 className="text-2xl md:text-3xl text-gray-500 font-bold text-center mb-4">
          Related Posts
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-3 gap-4 w-full">
          {relatedPosts.map((relatedPost) => (
            <div
              key={relatedPost.id}
              className="bg-white rounded-md overflow-hidden shadow-lg"
            >
              <Link to={`/blog-details/${relatedPost.id}`}>
                <img
                  src={relatedPost.blog_img}
                  alt=""
                  className="w-full h-40 object-cover rounded-t-md"
                />
              </Link>
              <div className="p-4">
                <Link to={`/blog-details/${relatedPost.id}`}>
                  <h5 className="mb-2 text-xl font-bold text-gray-900 dark:text-white">
                    {relatedPost.blog_title}
                  </h5>
                </Link>
                <p className="mb-3 text-sm text-gray-700 dark:text-gray-400">
                  {relatedPost.blog_description}
                </p>
                <Link
                  to={`/blog-details/${relatedPost.id}`}
                  className="inline-block px-4 py-2 text-sm font-bold text-white bg-gray-800 rounded-full hover:bg-gray-700 focus:outline-none"
                >
                  Read More
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BlogDetails;
