import React, { useState } from 'react';

const BestSellery = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % 4); // Assuming 4 items in the grid
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + 4) % 4);
  };

  return (
    <div>
      <div className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20">
        <div className="grid gap-10 sm:grid-cols-2 lg:grid-cols-4">
          {Array.from({ length: 6 }).map((_, index) => (
            <div key={index} className={`transform transition-transform duration-300 ${index === currentIndex ? 'opacity-100 scale-100' : 'opacity-50 scale-95 hover:scale-100'}`}>
              {/* Your existing code for the image grid */}
            </div>
          ))}
        </div>

        {/* Next and Previous buttons */}
        <div className="flex justify-center mt-8">
          <button onClick={handlePrev} className="mr-4 px-4 py-2 bg-gray-800 text-white rounded">
            Previous
          </button>
          <button onClick={handleNext} className="px-4 py-2 bg-gray-800 text-white rounded">
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default BestSellery;
