import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Class = () => {
  const [gifUrl, setGifUrl] = useState('');

  useEffect(() => {
    const fetchGif = async () => {
      try {
        // استبدال URL_ENDPOINT بعنوان الـ endpoint الخاص بالصورة GIF
        const response = await axios.get('https://www.genemedics.com/exercise-api', {
          responseType: 'arraybuffer',
        });

        // تحويل البيانات إلى صيغة base64
        const base64Data = Buffer.from(response.data, 'binary').toString('base64');

        // إعداد الصورة كرابط بيانات
        const imageDataUrl = `data:image/gif;base64,${base64Data}`;

        // تحديث حالة الصورة
        setGifUrl(imageDataUrl);
      } catch (error) {
        console.error('Error fetching GIF:', error);
      }
    };

    // استدعاء الدالة عند تحميل المكون
    fetchGif();
  }, []); // فارغ يعني أنه سيتم تشغيلها مرة واحدة عند تحميل المكون

  return (
    <div>
      {gifUrl && <img src={gifUrl} alt="GIF" />}
    </div>
  );
};

export default Class;
