import React, { useEffect, useState } from "react";
import axios from "axios";
import Profile from "../Components/users/Profile";
import { useCookies } from "react-cookie";
import { useNavigate } from "react-router-dom";

const Account = () => {
  const [user, setUser] = useState([]);
  const [page, setPage] = useState("profile");
  const [photoPreview, setPhotoPreview] = useState(null);
  const [cookies, setCookie, removeCookie] = useCookies(['token']);
  const history = useNavigate();

  // fetch products
  useEffect(() => {
    axios
      .get("http://localhost:5000/user")
      .then((response) => {
        setUser(response.data);
        setPhotoPreview(response.data.profile_image_name);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, []);

  function logout() {
    removeCookie('token');
    history("/");
  }

  // to open and close sidebar
  const [isSideOpen, setIsSideOpen] = useState(true);
  const [position, setPosition] = useState("left-0");

  function openSideBar() {
    if (isSideOpen) {
      setPosition("-left-64");
    } else {
      setPosition("left-0");
    }
    setIsSideOpen(!isSideOpen);
  }
  // to open and close sidebar //end

  return (
    <div className="flex flex-wrap">
      {/* sidebar */}
      <div className="relative w-[260px] h-full">
        <div
          className={`peer absolute top-0 border ${position} lg:left-0 h-full w-full object-cover transition-all delay-100 duration-1000`}
        >
          {/* rest of the sidebar code remains the same */}
        </div>
      </div>
      {/* content */}
      <div className={`${page === "profile" ? "block" : "hidden"} w-full`}>
        <Profile />
      </div>
    </div>
  );
};

export default Account;
