import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TrainerDetails = () => {
  const { trainerId } = useParams();
  const [trainerDetails, setTrainerDetails] = useState(null);

  useEffect(() => {
    const fetchTrainerDetails = async () => {
      try {
        const response = await axios.get(`https://fakestoreapi.com/products/${trainerId}`);
        setTrainerDetails(response.data);
      } catch (error) {
        console.error('Error fetching trainer details: ', error);
      }
    };

    fetchTrainerDetails();
  }, [trainerId]);

  // Render loading state while fetching data
  if (!trainerDetails) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>Trainer Details</h2>
      <p>Trainer ID: {trainerId}</p>
      <img src={trainerDetails.image} alt={trainerDetails.title} />
      <p>Name: {trainerDetails.title}</p>
      <p>Description: {trainerDetails.description}</p>
      <p>Pricing: {trainerDetails.price}</p>
      {/* Additional details about the trainer */}
    </div>
  );
};

export default TrainerDetails;
