import React, { useState } from 'react';

const BestSellery = () => {
  // Mock data for demonstration
  const mockData = [
    {
      id: 1,
      name: 'Oliver Aguilerra',
      role: 'Product Manager',
      description: "Vincent Van Gogh’s most popular painting, The Starry Night.",
      imageUrl: 'https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&dpr=3&h=750&w=1260',
    },
    // Add more data objects as needed
  ];

  const itemsPerPage = 8;
  const [currentPage, setCurrentPage] = useState(1);

  const paginatedData = mockData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);
  const totalPages = Math.ceil(mockData.length / itemsPerPage);

  const handleNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages));
  };

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  return (
    <div>
      <div className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20">
        <h1 className="text-gray-800 text-4xl mb-6 font-bold item-center justify-center text-center">
          Beast Trainers
        </h1>
        <div className="grid gap-10 sm:grid-cols-2 lg:grid-cols-4">
          {paginatedData.map((item) => (
            <div key={item.id}>
              <div className="relative overflow-hidden transition duration-300 transform rounded shadow-lg lg:hover:-translate-y-2 hover:shadow-2xl">
                <img
                  className="object-cover w-full h-56 md:h-64 xl:h-80"
                  src={item.imageUrl}
                  alt={item.name}
                />
                <div className="absolute inset-0 flex flex-col justify-center px-5 py-4 text-center transition-opacity duration-300 bg-black bg-opacity-75 opacity-0 hover:opacity-100">
                  <p className="mb-1 text-lg font-bold text-gray-100">{item.name}</p>
                  <p className="mb-4 text-xs text-gray-100">{item.role}</p>
                  <p className="mb-4 text-xs tracking-wide text-gray-400">{item.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Pagination */}
        <div className="flex justify-center mt-8">
          <button
            onClick={handlePrevPage}
            disabled={currentPage === 1}
            className="mr-4 px-4 py-2 bg-gray-800 text-white rounded"
          >
            Previous
          </button>
          <button
            onClick={handleNextPage}
            disabled={currentPage === totalPages}
            className="px-4 py-2 bg-gray-800 text-white rounded"
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default BestSellery;
