// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ExerciseCard = ({ exercise }) => {
  return (
    <div className="max-w-sm rounded overflow-hidden shadow-lg bg-white m-4">
      <img
        className="w-full h-40 object-cover"
        src={exercise.photo} // Assuming your API response includes a 'photo' field
        alt={exercise.title} // Use the title as alt text for accessibility
      />
      <div className="px-6 py-4">
        <div className="font-bold text-xl mb-2">{exercise.title}</div>
        <p className="text-gray-700 text-base">{exercise.paragraph}</p>
      </div>
    </div>
  );
};

const App = () => {
  const [exercises, setExercises] = useState([]);

  useEffect(() => {
    const fetchExercises = async () => {
      try {
        const response = await axios.get('https://www.genemedics.com/exercise-api');
        setExercises(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchExercises();
  }, []);

  return (
    <div className="text-center my-8">
      <h1 className="text-4xl font-bold mb-4">Exercise Cards</h1>
      <div className="flex flex-wrap justify-center">
        {exercises.map((exercise) => (
          <ExerciseCard key={exercise.id} exercise={exercise} />
        ))}
      </div>
    </div>
  );
};

export default App;
