import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";

const DisProducts = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [blogImages, setBlogImages] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/products/discount/${id}`)
      .then((response) => {
        console.log(response.data); // Log the API response
        setProducts(response.data.data);
        setBlogImages(response.data.images);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error:", error);
        setLoading(false);
      });
  }, [id]);

  const addToCart = async () => {
    try {
      const response = await axios.post("https://fakestoreapi.com/carts", {
        id: id,
      });
      if (response.status === 200) {
        alert("Added to cart successfully!");
      }
    } catch (error) {
      console.log("Error adding to cart:", error);
    }
  };

  return (
    <div className="my-16">
      <h1 className="text-teal-600 text-4xl mb-6 font-bold">Explore Our Products</h1>
      <div className="relative flex flex-wrap gap-7 justify-center items-center mx-16">
        {loading ? (
          <p>Loading...</p>
        ) : (
          products.map((product) => (
            <div
              key={product.product_id}
              className="group my-2 flex flex-col md:flex-row flex-wrap w-full max-w-xs flex-col overflow-hidden rounded-lg border border-gray-100 bg-white shadow-md"
            >
              <Link
                className="relative mx-3 mt-3 flex h-60 overflow-hidden rounded-xl"
                to={`/product/${product.id}`}
              >
                <img
                  className="peer absolute border top-0 right-0 h-full w-full object-cover"
                  src={blogImages[0].image_url}
                  alt="product image"
                />
              </Link>
              <div className="mt-4 px-5 pb-5">
                <a href="#">
                  <h5 className="text-xl text-start h-8 mb-5 overflow-hidden tracking-tight text-slate-900">
                    {product.product_name}
                  </h5>
                </a>
                <div className="mt-2 mb-5 flex items-center justify-between">
                  <p>
                    <span className="text-lg font-bold text-slate-900">
                      ${product.price}
                    </span>
                  </p>
                </div>
                <button
                  onClick={addToCart}
                  className="w-full flex items-center justify-center rounded-full bg-teal-600 px-5 py-2.5 text-center text-sm font-medium text-white hover:bg-teal-800 focus:outline-none focus:ring-4 focus:ring-blue-300"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    className="mr-2 h-6 w-6"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                    stroke-width="2"
                  >
                    <path
                      stroke-linecap="round"
                      stroke-linejoin="round"
                      d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z"
                    />
                  </svg>
                  Add to cart
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default DisProducts;
