// في ملف Category.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AOS from "aos";
import "aos/dist/aos.css";

const Category = () => {
  const [data, setData] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const productPerPage = 4; // Number of products per page
  const [visibleItems, setVisibleItems] = useState(productPerPage); 

  // Fetch products from the API
  useEffect(() => {
    AOS.init();
    axios
      .get("http://localhost:3000/category")
      .then((response) => {
        setData(response.data); // Set the product data
      })
      .catch((error) => {
        // Handle errors here
        console.error("Error:", error);
      });
  }, []);

  return (
    <div data-aos="fade-up" className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20 ">
      <h1 className="text-[#27374D] text-4xl mb-6 font-bold item-center justify-center text-center">Category</h1>
      <div className="grid gap-10 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-2 w-full">
        {data.slice(0, visibleItems).map((item, index) => (
          <Link to={`/category/${item.title}`} key={index}>
            <div className="relative overflow-hidden transition duration-300 transform rounded shadow-lg lg:hover:-translate-y-2 hover:shadow-2xl h-[20rem]">
              <img
                className="object-cover w-full h-[20rem]"
                src={item.image}
                alt={`Product Image ${index + 1}`}
              />
              {/* Additional content here */}
              <div className="absolute inset-0 flex flex-col justify-start px-5 py-4 text-start transition-opacity duration-300">
                <p className="font-bold text-white text-1xl">{item.title}</p>
                <div className="flex items-center justify-center space-x-3">
                  {/* Additional content here */}
                </div>
              </div>
            </div>
          </Link>
        ))}
      </div>
     <Link to="/CategoryItems"
        className="p-2 bg-gray-800 text-white rounded-xl mt-2 hover:bg-gray-600"
        onClick={() => setVisibleItems((prev) => prev + productPerPage)}
      >
        Show More
      </Link>
    </div>
  );
};

export default Category;
