import React, { useState } from 'react';

const Faqs = () => {
  const [questionStates, setQuestionStates] = useState({
    question1: 'closed',
    question2: 'closed',
    question3: 'closed',
    question4: 'closed',
  });

  const toggleAnswer = (questionId) => {
    setQuestionStates((prevStates) => ({
      ...prevStates,
      [questionId]: prevStates[questionId] === 'closed' ? 'open' : 'closed',
    }));
  };

  return (
    <div>
      <section className="py-10 bg-gray-50 sm:py-16 lg:py-24 ">
        <div className="px-4 mx-auto sm:px-6 lg:px-8 max-w-7xl">
          <div className="max-w-2xl mx-auto text-center">
            <h2 className="text-3xl font-bold leading-tight text-black sm:text-4xl lg:text-5xl">
              Explore Common Questions
            </h2>
          </div>
          <div className="max-w-3xl mx-auto mt-8 space-y-4 md:mt-16 ">
            {Array.from({ length: 4 }, (_, index) => (
              <div
                key={`question${index + 1}`}
                className="transition-all duration-200 bg-white border border-gray-200 shadow-lg cursor-pointer hover:bg-gray-50"
              >
                <button
                  type="button"
                  id={`question${index + 1}`}
                  data-state={questionStates[`question${index + 1}`]}
                  onClick={() => toggleAnswer(`question${index + 1}`)}
                  className="flex items-center justify-between w-full px-4 py-5 sm:p-6"
                >
                  <span className="flex text-lg font-semibold text-black">
                    {`How can I get started?`}
                  </span>
                  <svg
                    id={`arrow${index + 1}`}
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                    className="w-6 h-6 text-gray-400"
                  >
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                  </svg>
                </button>
                <div
                  id={`answer${index + 1}`}
                  style={{ display: questionStates[`question${index + 1}`] === 'open' ? 'block' : 'none' }}
                  className="px-4 pb-5 sm:px-6 sm:pb-6"
                >
                  <p>
                    Getting started is easy! Sign up for an account, and you'll have access to our
                    platform's features. No credit card required for the initial signup.
                  </p>
                </div>
              </div>
            ))}
          </div>
          <p className="text-center text-gray-600 textbase mt-9">
            Still have questions?
            <span className="cursor-pointer font-medium text-tertiary transition-all duration-200 hover:text-tertiary focus:text-tertiary hover-underline">
              Contact our support
            </span>
          </p>
        </div>
      </section>
    </div>
  );
};

export default Faqs;
