import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import queryString from 'query-string';

const Pricing = () => {
  const { id } = useParams();
  const [pricingData, setPricingData] = useState([]);
  const [createModalOpen, setCreateModalOpen] = useState(false);
  const [updateModalOpen, setUpdateModalOpen] = useState(false);
  const [newPlan, setNewPlan] = useState({ name: '', price: '', description: '' });
  const [selectedPlan, setSelectedPlan] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post(`http://localhost:8080/getplansfortrainer/${id}`);
        // Check if the response is an array or a single object
        const data = Array.isArray(response.data) ? response.data : [response.data];
        setPricingData(data);
      } catch (error) {
        console.error('Error fetching pricing data:', error);
      }
    };

    fetchData();
  }, [id]);

  const handleBuyNow = (price) => {
    const queryParams = queryString.stringify({ price });
    window.location.href = `/payment?${queryParams}`;
  };

  const handleCreatePlan = async () => {
    try {
      const response = await axios.post('http://localhost:8080/createplan', newPlan);
      setPricingData([...pricingData, response.data]);
      setCreateModalOpen(false);
      setNewPlan({ name: '', price: '', description: '' });
    } catch (error) {
      console.error('Error creating pricing plan:', error);
    }
  };

  const handleUpdatePlan = async () => {
    if (!selectedPlan) return;

    try {
      const response = await axios.put(`http://localhost:8080/updateplan/${selectedPlan.id}`, selectedPlan);
      setPricingData(pricingData.map(plan => (plan.id === response.data.id ? response.data : plan)));
      setUpdateModalOpen(false);
      setSelectedPlan(null);
    } catch (error) {
      console.error('Error updating pricing plan:', error);
    }
  };

  const handleDeletePlan = async (planId) => {
    try {
      await axios.delete(`http://localhost:8080/deleteplan/${planId}`);
      setPricingData(pricingData.filter(plan => plan.id !== planId));
    } catch (error) {
      console.error('Error deleting pricing plan:', error);
    }
  };

  return (
    <div>
      {/* ... (your existing code) */}
      {/* Add a button to open the create modal */}
      <button onClick={() => setCreateModalOpen(true)}>Create Plan</button>

      {/* Create Plan Modal */}
      {createModalOpen && (
        <div>
          <div>
            <label>Name:</label>
            <input type="text" value={newPlan.name} onChange={(e) => setNewPlan({ ...newPlan, name: e.target.value })} />
          </div>
          <div>
            <label>Price:</label>
            <input type="text" value={newPlan.price} onChange={(e) => setNewPlan({ ...newPlan, price: e.target.value })} />
          </div>
          <div>
            <label>Description:</label>
            <input type="text" value={newPlan.description} onChange={(e) => setNewPlan({ ...newPlan, description: e.target.value })} />
          </div>
          <button onClick={handleCreatePlan}>Create</button>
          <button onClick={() => setCreateModalOpen(false)}>Cancel</button>
        </div>
      )}

      {/* Update Plan Modal */}
      {updateModalOpen && selectedPlan && (
        <div>
          <div>
            <label>Name:</label>
            <input type="text" value={selectedPlan.name} onChange={(e) => setSelectedPlan({ ...selectedPlan, name: e.target.value })} />
          </div>
          <div>
            <label>Price:</label>
            <input type="text" value={selectedPlan.price} onChange={(e) => setSelectedPlan({ ...selectedPlan, price: e.target.value })} />
          </div>
          <div>
            <label>Description:</label>
            <input type="text" value={selectedPlan.description} onChange={(e) => setSelectedPlan({ ...selectedPlan, description: e.target.value })} />
          </div>
          <button onClick={handleUpdatePlan}>Update</button>
          <button onClick={() => setUpdateModalOpen(false)}>Cancel</button>
        </div>
      )}

      {/* Pricing plans with delete button */}
      {pricingData.map((item, index) => (
        <div key={index}>
          <h2>{item.name}</h2>
          <p>{item.price}</p>
          <p>{item.description}</p>
          <button onClick={() => handleBuyNow(item.price)}>Buy Now</button>
          <button onClick={() => { setUpdateModalOpen(true); setSelectedPlan(item); }}>Update</button>
          <button onClick={() => handleDeletePlan(item.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default PlanPricing;
