import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';

const BestTrainers = () => {
  const [trainers, setTrainers] = useState([]);

  useEffect(() => {
    const fetchTrainers = async () => {
      AOS.init();
      try {
        const response = await axios.get('http://localhost:8080/getAllTrainers');
        setTrainers(response.data);
        
      } catch (error) {
        console.error('Error fetching data:', error);
  
        // Additional information from Axios error
        if (error.response) {
          console.error('Response data:', error.response.data);
          console.error('Response status:', error.response.status);
        }
      }
    };
  
    fetchTrainers();
  }, []);
  // Check if trainers is an array before using slice
  const firstFourTrainers = Array.isArray(trainers) ? trainers.slice(0, 4) : [];

  return (
    <div data-aos="fade-up" className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20 mt-24">
      <h1 className="text-[#27374D] text-4xl mb-6 font-bold text-center">Best Trainers</h1>
      <div className="grid gap-10 sm:grid-cols-2 lg:grid-cols-4">
        {firstFourTrainers.map((trainer) => (
          <div key={trainer.trainer_id} className="group">
            <Link to={`/trainers/${trainer.trainer_id}`}>
              <div className="relative overflow-hidden rounded-lg shadow-xl border-2 border-[#1A202C] transition duration-300 transform hover:-translate-y-2 hover:shadow-2xl">
                <img
                  className="object-cover w-full h-56 md:h-64 xl:h-80 rounded-t-lg"
                  src={trainer.image}
                  alt="Person"
                />
                <div className="absolute inset-0 flex flex-col justify-center px-5 py-4 text-center transition-opacity duration-300 bg-[#2D3748] bg-opacity-90 opacity-0 group-hover:opacity-100">
                  <p className="mb-1 text-lg font-bold text-[#27374D] group-hover:text-white">
                    {trainer.username}
                  </p>
                  <p className="mb-1 text-xs text-[#6B7280] group-hover:text-white">
                    Certification: {trainer.certification}
                  </p>
                  <p className="mb-1 text-xs text-[#6B7280] group-hover:text-white">
                    Experience: {trainer.experience} years
                  </p>
                  <p className="mb-4 text-xs text-[#6B7280] group-hover:text-white">
                    Rating: {trainer.rating}
                  </p>
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BestTrainers;
