import React, { useEffect, useState } from "react";
import axios from "axios";

import { useCookies } from "react-cookie";
import { useNavigate } from "react-router-dom";
import Contactus from  "../admin1/Contactus";
import Courses from "../admin1/Courses";
import Blogs from "../admin1/Blogs";
import Users from "../admin1/Users";
import Dashboard from "../admin1/Dashboard";

const Account = () => {
  const [user, setUser] = useState([]);
  const [page, setPage] = useState("profile");
  const [photoPreview, setPhotoPreview] = useState(null);
  const [cookies, setCookie, removeCookie] = useCookies(['token']);
  const history = useNavigate();

  useEffect(() => {
    axios
      .get("http://localhost:5000/user")
      .then((response) => {
        setUser(response.data);
        setPhotoPreview(response.data.profile_image_name);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });
  }, []);

  function logout() {
    removeCookie('token');
    history("/");
  }

  const [isSideOpen, setIsSideOpen] = useState(true);

  const toggleSidebar = () => {
    setIsSideOpen(!isSideOpen);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className={`fixed h-full bg-gray-900 text-white transition-all duration-300 ${isSideOpen ? 'w-64' : 'w-0'}`}>
        <button
          aria-label="Toggle sidebar"
          className="h-10 w-10 bg-gray-600 absolute top-4 right-4 flex items-center justify-center cursor-pointer focus:outline-none focus:ring-2 focus:ring-offset-2 rounded focus:ring-gray-800"
          onClick={toggleSidebar}
        >
          {/* ... (رمز الهامبورغر أو أي رمز آخر لتحديد فتح/إغلاق السلايدر) */}
        </button>
        {/* ... (محتوى السلايدر هنا) */}
      </div>

      {/* Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Navbar */}
        <nav className="flex items-center justify-between bg-gray-800 p-4">
          {/* ... (عناصر شريط التنقل هنا) */}
        </nav>

        {/* Page Content */}
        <div className="flex-1 overflow-x-hidden overflow-y-auto p-5">
          {/* Display selected page */}
          {page === "Dashboard" && <Dashboard />}
          {page === "Users" && <Users />}
          {page === "Blogs" && <Blogs />}
          {page === "Courses" && <Courses />}
          {page === "Contactus" && <Contactus />}
        </div>

        {/* Footer */}
        <footer className="bg-gray-800 text-white text-center py-2">
          {/* ... (محتوى الfooter هنا) */}
        </footer>
      </div>
    </div>
  );
};

export default Account;
