import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Category = () => {
  const [data, setData] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const productPerPage = 4; // Number of products per page

  // Static image URLs
  const staticImages = [
    'https://i.pinimg.com/736x/54/cb/3e/54cb3edb4c34fd9e4df0f27531a86f69.jpg',
    'https://i.insider.com/5c5ae009342cca01c71cf984?width=1136&format=jpeg',
    'https://pbs.twimg.com/media/D5ImZX7X4AAlHIN.jpg',
    'https://i.pinimg.com/736x/3b/01/a4/3b01a4cacda69e60988c3858fe191a4a.jpg',
  ];
 // Static titles
 const staticTitles = [
  'fitness',
  'CROSSFIT',
  'CARDIO',
"  BODY BUILDING",
'kickboxing',
];
  // Fetch products from the API
  useEffect(() => {
    axios
    //error 
      .get("https://fakestoreapi.com/products")
      .then((response) => {
        // Set the product data
        setData(response.data.slice(0, 4)); // Limit the data to the first 4 items
      })
      .catch((error) => {
        // Handle errors here
        console.error("Error:", error);
      });
  }, []);

  return (
    <div className="px-4 py-16 mx-auto sm:max-w-xl md:max-w-full lg:max-w-screen-xl md:px-24 lg:px-8 lg:py-20">
      <h1 className="text-teal-600 text-4xl mb-6 font-bold item-center justify-center text-center">Category</h1>
      <div className="grid gap-10 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-2 mx-[6rem] ">
        {staticTitles.map((title, index) => (
          <Link to={`/category/${title}`} key={index}>
            <div className="relative overflow-hidden transition duration-300 transform rounded shadow-lg lg:hover:-translate-y-2 hover:shadow-2xl ">
              <img
                className="object-cover w-full h-64"
                src={staticImages[index]}
                alt={`Static Image ${index + 1}`}
              />
              {/* Additional content here */}
              <div className="absolute inset-0 flex flex-col justify-start px-5 py-4 text-start transition-opacity duration-300">
                <p className=" font-bold text-white text-1xl" >{staticTitles[index]}</p>
                {/* <p className="text-xs text-teal-600">{item.category}</p> */}

                <div className="flex items-center justify-center space-x-3">
                  {/* Additional content here */}
                </div>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Category;
