import React, { useEffect, useState } from 'react';
import axios from 'axios';

const NutritionFacts = () => {
  const [nutritionData, setNutritionData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/Nutrition');
        setNutritionData(response.data);
      } catch (error) {
        console.error('Error fetching nutrition data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="bg-white rounded shadow-md max-w-xl mx-auto mt-4 p-4">
      <div className="text-2xl font-bold">Bodybuilder's Nutrition Facts</div>
      {nutritionData ? (
        <table className="table w-full border border-black border-collapse">
          <thead>
            <tr className="border-b border-black">
              <th className="py-2 font-medium border-r border-black">Nutrient</th>
              <th className="py-2 border-r border-black">Amount</th>
              <th className="py-2 border-l border-black">% Daily Value</th>
            </tr>
          </thead>
          <tbody>
            <tr className="border-b border-black">
              <td className="py-2 font-medium border-r border-black">Serving Size</td>
              <td className="py-2 border-r border-black">1 cup (240g)</td>
              <td className="py-2 border-l border-black"></td>
            </tr>
            {/* يمكنك استخدام بيانات التغذية هنا */}
          </tbody>
        </table>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default NutritionFacts;
