import React from 'react';

// Statistical Card component
const StatCard = ({ icon, value, label, color }) => {
  return (
    <div className={'bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition duration-300 hover:scale-105 text-center w-[20rem] flex flex-wrap flex-row just'}>
      <svg
        fill="none"
        stroke={`currentColor`}
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth={2}
        className={`text-${color}-500 w-12 h-12 mx-auto mb-3`}
        viewBox="0 0 24 24"
      >
        {/* Statistical Icon */}
        {getIcon(icon)}
      </svg>
      <h2 className="text-2xl font-medium text-gray-900">{value}</h2>
      <p className="text-sm text-gray-600">{label}</p>
    </div>
  );
};

// Function to get the icon based on its name
const getIcon = (icon) => {
  switch (icon) {
    case 'adjustments':
      return (
        <>
          <path d="M8 17l4 4 4-4m-4-5v9" />
          <path d="M20.88 18.09A5 5 0 0018 9h-1.26A8 8 0 103 16.29" />
        </>
      );
    case 'users':
      return (
        <>
          <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
          <circle cx={9} cy={7} r={4} />
          <path d="M23 21v-2a4 4 0 00-3-3.87m-4-12a4 4 0 010 7.75" />
        </>
      );
    case 'folder':
      return (
        <>
          <path d="M3 18v-6a9 9 0 0118 0v6" />
          <path d="M21 19a2 2 0 01-2 2h-1a2 2 0 01-2-2v-3a2 2 0 012-2h3zM3 19a2 2 0 002 2h1a2 2 0 002-2v-3a2 2 0 00-2-2H3z" />
        </>
      );
    case 'location-marker':
      return (
        <>
          <path d="M3 18v-6a9 9 0 0118 0v6" />
          <path d="M21 19a2 2 0 01-2 2h-1a2 2 0 01-2-2v-3a2 2 0 012-2h3zM3 19a2 2 0 002 2h1a2 2 0 002-2v-3a2 2 0 00-2-2H3z" />
        </>
      );
    default:
      return null;
  }
};

// Dashboard component
const Dashboard = () => {
  return (
    <div className="container mx-auto px-4 py-12">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
        {/* Statistical Card 1 */}
        <StatCard icon="adjustments" value="2.7K" label="Downloads" color="indigo" />
        {/* Statistical Card 2 */}
        <StatCard icon="users" value="1.3K" label="Users" color="indigo" />
        {/* Statistical Card 3 */}
        <StatCard icon="folder" value="74" label="Files" color="indigo" />

      </div>
    </div>
  );
};

export default Dashboard;
