import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WorkoutRoutineComponent = () => {
  const [workoutData, setWorkoutData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Replace 'YOUR_API_ENDPOINT' with the actual API endpoint
        const response = await axios.get('http://localhost:3000/Workout');
        setWorkoutData(response.data);
      } catch (error) {
        console.error('Error fetching workout data:', error);
      }
    };

    fetchData();
  }, []); // The empty dependency array ensures that the effect runs only once after the component mounts

  return (
    <div className="bg-white rounded shadow-md max-w-xl mx-auto mt-4 p-4 ">
      <div className="text-2xl font-bold">Workout Routine</div>
      <table className="table w-full border border-black border-collapse">
        <thead>
          <tr className="border-b border-black">
            <th className="py-2 font-medium border-r border-black">Exercise</th>
            <th className="py-2 border-r border-black">Repetitions</th>
            <th className="py-2 border-l border-black">Rest</th>
          </tr>
        </thead>
        <tbody>
          {/* Check if workoutData exists before mapping over it */}
          {workoutData &&
            workoutData.map((exercise, index) => (
              <tr key={index} className="border-b border-black">
                <td className="py-2 font-medium border-r border-black">{exercise.name}</td>
                <td className="py-2 border-r border-black">{exercise.repetitions}</td>
                <td className="py-2 border-l border-black">{exercise.rest}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default WorkoutRoutineComponent;

