// BlogDetails.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const BlogDetails = () => {
  const [blog, setBlog] = useState({});
  const { id } = useParams();

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/posts/${id}`);
        setBlog(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchBlog();
  }, [id]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <h1 className="text-gray-800 text-4xl mb-6 font-bold">Blog Details</h1>
      <div className="bg-white rounded-md overflow-hidden shadow-lg w-[25rem]">
        <img
          src="https://i.ytimg.com/vi/gey73xiS8F4/maxresdefault.jpg"
          alt=""
          className="w-[25rem] h-40 object-cover"
        />
        <div className="p-4 w-[25rem]">
          <h5 className="w-[25rem] mb-2 text-xl font-bold text-gray-900 dark:text-white">
            {blog.title}
          </h5>
          <p className="w-[25rem] mb-3 text-sm text-gray-700 dark:text-gray-400">
            {blog.body}
          </p>
        </div>
      </div>
    </div>
  );
};

export default BlogDetails;
