import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AOS from 'aos';
import 'aos/dist/aos.css';

const AchievementCard = ({ value, description }) => (
  <div
    className="flex flex-col justify-center items-center bg-gradient-to-r from-[#F28767] to-[#FDB079] px-8 py-12 md:px-16 md:py-20 rounded-lg justify-self-center overflow-hidden shadow-lg"
    data-aos="fade-up"
    data-aos-duration="1000"
    data-aos-delay="200"
  >
    <div className="flex flex-row justify-center items-center mb-4">
      <p className="font-bold text-4xl sm:text-5xl lg:text-6xl leading-10 text-white ml-2">{value}</p>
    </div>
    <p className="font-medium text-lg sm:text-xl leading-6 text-center text-white">{description}</p>
  </div>
);

const Statestic = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    AOS.init();
    // Make an Axios request to fetch data
    axios.get('http://localhost:3000/statestic')
      .then(response => {
        // Assuming your API response is an array of objects with 'value' and 'description' properties
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []); // The empty dependency array ensures that the effect runs only once when the component mounts

  return (
    <div>
      <section className="flex flex-col mt-20">
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-y-5 lg:gap-y-0 gap-x-5 place-items-center w-full mx-auto max-w-7xl px-5">
          {data.map((item, index) => (
            <AchievementCard key={index} value={item.value} description={item.description} />
          ))}
        </div>
      </section>
    </div>
  );
}

export default Statestic;
