import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

const BlogDetails = () => {
  const [blog, setBlog] = useState({});
  const [relatedPosts, setRelatedPosts] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ user: "", comment: "" });
  const { id } = useParams();

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/posts/${id}`);
        setBlog(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    const fetchRelatedPosts = async () => {
      try {
        const response = await axios.get("https://jsonplaceholder.typicode.com/posts");
        const filteredRelatedPosts = response.data.slice(-3);
        setRelatedPosts(filteredRelatedPosts);
      } catch (error) {
        console.error("Error fetching related posts: ", error);
      }
    };

    const fetchReviews = async () => {
      try {
        // Assuming you have an endpoint for fetching reviews
        const response = await axios.get("https://jsonplaceholder.typicode.com/comments");
        setReviews(response.data);
      } catch (error) {
        console.error("Error fetching reviews: ", error);
      }
    };

    fetchBlog();
    fetchRelatedPosts();
    fetchReviews();
  }, [id]);

  const handleReviewChange = (e) => {
    const { name, value } = e.target;
    setNewReview((prevReview) => ({ ...prevReview, [name]: value }));
  };

  const handleReviewSubmit = (e) => {
    e.preventDefault();
    if (newReview.user && newReview.comment) {
      const updatedReviews = [...reviews, { id: reviews.length + 1, ...newReview }];
      setReviews(updatedReviews);
      setNewReview({ user: "", comment: "" });
    }
  };

  const renderReviews = () => {
    return reviews.map((review) => (
      <div key={review.id} className="mb-4">
        <strong>{review.name}:</strong> {review.body}
      </div>
    ));
  };

  return (
    <div className="mt-6 bg-gray-50">
      <div className="px-10 py-6 mx-auto max-w-6xl">
        <div className="max-w-4xl mx-auto bg-white rounded-md overflow-hidden shadow-lg">
          <img
            src="https://i.ytimg.com/vi/gey73xiS8F4/maxresdefault.jpg"
            alt=""
            className="w-full h-64 object-cover"
          />
          <div className="p-8">
            <div className="flex items-center justify-start mt-4 mb-4"></div>
            <div className="mt-2">
              <a
                href="#"
                className="sm:text-3xl md:text-3xl lg:text-3xl xl:text-4xl font-bold text-purple-500 hover:underline"
              >
                {blog.title}
              </a>
              <div className="flex justify-start items-center mt-2"></div>
              <div className="font-light text-gray-600">
                <a href="#" className="flex items-center mt-6 mb-6"></a>
              </div>
            </div>
            <div className="max-w-4xl px-10 mx-auto text-2xl text-gray-700 mt-4 rounded bg-gray-100">
              <div>
                <p className="mt-2 p-8">{blog.body}</p>
              </div>
            </div>
          </div>
        </div>
        <h2 className="text-2xl mt-4 text-gray-500 font-bold text-center">
          Related Posts
        </h2>
        <div className="flex grid h-full grid-cols-12 gap-10 pb-10 mt-8 sm:mt-16">
          {relatedPosts.map((relatedPost) => (
            <div key={relatedPost.id} className="col-span-4">
              <Link to={`/blog-details/${relatedPost.id}`}>
                <img
                  src="https://i.ytimg.com/vi/gey73xiS8F4/maxresdefault.jpg"
                  alt=""
                  className="w-full h-40 object-cover"
                />
              </Link>
              <div className="p-4">
                <Link to={`/blog-details/${relatedPost.id}`}>
                  <h5 className="mb-2 text-xl font-bold text-gray-900 dark:text-white">
                    {relatedPost.title}
                  </h5>
                </Link>
                <p className="mb-3 text-sm text-gray-700 dark:text-gray-400">
                  {relatedPost.body}
                </p>
                <Link
                  to={`/blog-details/${relatedPost.id}`}
                  className="inline-block px-4 py-2 text-sm font-bold text-white bg-gray-800 rounded-full hover:bg-gray-700 focus:outline-none"
                >
                  Read More
                </Link>
              </div>
            </div>
          ))}
        </div>

        {/* Review Section */}
        <div className="mt-8">
          <h2 className="text-2xl text-gray-500 font-bold mb-4">Reviews</h2>
          {renderReviews()}

          {/* Review Form */}
          <form onSubmit={handleReviewSubmit} className="mt-4">
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="user">
                Your Name:
              </label>
              <input
                type="text"
                id="user"
                name="user"
                value={newReview.user}
                onChange={handleReviewChange}
                className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="comment">
                Your Comment:
              </label>
              <textarea
                id="comment"
                name="comment"
                value={newReview.comment}
                onChange={handleReviewChange}
                className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>
            <button
              type="submit"
              className="bg-gray-800 text-white py-2 px-4 rounded-full hover:bg-gray-700 focus:outline-none"
            >
              Submit Review
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BlogDetails;
