// UpdatePlan.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";

const UpdatePlan = ({ courseId, onClose }) => {
  const [course, setCourse] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCourseDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/Corse/${courseId}`);
        setCourse(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching course details: ", error);
        setLoading(false);
      }
    };

    fetchCourseDetails();
  }, [courseId]);

  const handleUpdate = async () => {
    try {
      // Implement the update logic here using axios.put or axios.patch
      // ...

      // Close the popup after a successful update
      onClose();
    } catch (error) {
      console.error("Error updating course: ", error);
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Update Course</h2>
      <form>
        {/* Add form fields to update course details */}
        {/* For example: */}
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="courseName">
            Course Name
          </label>
          <input
            type="text"
            id="courseName"
            name="courseName"
            value={course.Course_Name}
            onChange={(e) => setCourse({ ...course, Course_Name: e.target.value })}
            className="bg-gray-100 border-2 border-gray-300 p-2 w-full rounded-md"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="courseDescription">
            Course Description
          </label>
          <textarea
            id="courseDescription"
            name="courseDescription"
            value={course.Course_description}
            onChange={(e) => setCourse({ ...course, Course_description: e.target.value })}
            className="bg-gray-100 border-2 border-gray-300 p-2 w-full rounded-md"
          />
        </div>

        {/* Add more form fields as needed */}

        <div className="flex justify-end">
          <button
            type="button"
            onClick={handleUpdate}
            className="bg-blue-500 text-white py-2 px-4 rounded-full hover:bg-blue-700 focus:outline-none"
          >
            Update
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdatePlan;
