// Blog.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Blog = () => {
  const [blog, setBlog] = useState({});
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/blog${id}`);
        setBlog(response.data);
      } catch (error) {
        console.error("Error fetching data: ", error);
      }
    };

    fetchData();
  }, [id]);

  return (
    <div className="min-h-screen">
      <h1 className="text-gray-800 text-4xl mb-6 font-bold text-center">{blog.blog_title}</h1>
      <div className="max-w-2xl mx-auto">
        <img src={blog.blog_img} alt="" className="w-full h-auto mb-4" />
        <p className="text-sm text-gray-700 dark:text-gray-400 mb-4">{blog.blog_subdescription}</p>
        <p className="text-base text-gray-800 dark:text-white">{blog.blog_content}</p>
      </div>
    </div>
  );
};

export default Blog;
