import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BestTrainers = () => {
  const itemsPerPage = 12; // Number of trainers to display per page
  const [trainers, setTrainers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://jsonplaceholder.typicode.com/posts'); // Replace 'YOUR_API_ENDPOINT' with the actual endpoint URL
        setTrainers(response.data); // Assuming the response contains an array of trainers
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures this effect runs once when the component mounts

  const indexOfLastTrainer = currentPage * itemsPerPage;
  const indexOfFirstTrainer = indexOfLastTrainer - itemsPerPage;
  const currentTrainers = trainers.slice(indexOfFirstTrainer, indexOfLastTrainer);

  const totalPages = Math.ceil(trainers.length / itemsPerPage);

  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) {
      setCurrentPage(pageNumber);
    }
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }
    return pageNumbers;
  };

  return (
    <div>
      {/* ... Your existing code ... */}
      <div className="grid gap-10 sm:grid-cols-2 lg:grid-cols-4">
        {currentTrainers.map((trainer, index) => (
          <div key={index}>
            {/* ... Your existing trainer card code ... */}
          </div>
        ))}
      </div>
      {/* Pagination */}
      <div className="flex justify-center mt-4">
        <button
          className={`mx-1 px-3 py-2 bg-gray-300 text-gray-800`}
          onClick={() => paginate(currentPage - 1)}
        >
          Prev
        </button>
        {renderPageNumbers().map((number) => (
          <button
            key={number}
            className={`mx-1 px-3 py-2 ${
              currentPage === number ? 'bg-gray-800 text-white' : 'bg-gray-300 text-gray-800'
            }`}
            onClick={() => paginate(number)}
          >
            {number}
          </button>
        ))}
        <button
          className={`mx-1 px-3 py-2 bg-gray-300 text-gray-800`}
          onClick={() => paginate(currentPage + 1)}
        >
          Next
        </button>
      </div>
      {/* ... Other existing code ... */}
    </div>
  );
};

export default BestTrainers;
